import React, { useState } from "react";
import { Box, Flex, Link, Button, IconButton, useDisclosure } from "@chakra-ui/react";
import { HamburgerIcon, CloseIcon } from "@chakra-ui/icons";
import logo from "../assets/logo.png";
import "../styles/NavBar.css";

const NavBar = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const contact = "https://wa.me/6281215267243";

  return (
    <>
      <Box>
        <Flex
          top="0"
          left="0"
          right="0"
          zIndex="999"
          justifyContent="space-between"
          className="myNavBar"
        >
          <Box
            paddingLeft={{ base: "0", md: "40px" }}
            color="white"
            display="flex"
            justifyContent={{ base: "center", md: "flex-start" }}
          >
            <Link href="/">
              <img
                src={logo}
                alt="Solo Mina Corp"
                width={{ base: "150px", md: "200px" }}
                height={{ base: "150px", md: "200px" }}
                style={{ borderRadius: "5px" }}
              />
            </Link>
          </Box>
          <Box display={{ base: "block", md: "none" }} paddingRight="20px">
            <IconButton
              icon={isOpen ? <CloseIcon /> : <HamburgerIcon />}
              onClick={isOpen ? onClose : onOpen}
              variant="ghost"
              size="md"
              color="white"
            />
          </Box>
          <Box
            display={{ base: isOpen ? "block" : "none", md: "flex" }}
            flexDirection={{ base: "column", md: "row" }}
            justifyContent="space-between"
            gap="20px"
            alignContent="flex-end"
            paddingRight={{ base: "20px", md: "40px" }}
            marginTop={{ base: isOpen ? "4" : "0", md: "0" }}
          >
            <Link color="black" href="/" onClick={onClose}>
              Home
            </Link>
            <Link color="black" href="/aboutus" onClick={onClose}>
              About
            </Link>
            <Link color="black" href="/faq" onClick={onClose}>
              FAQ
            </Link>
            <Link color="black" href={contact} target="blank" onClick={onClose}>
              Contact
            </Link>
          </Box>
          <Box
            display={{ base: "flex", md: "block" }}
            alignItems="center"
            gap="20px"
            justifyContent={{ base: "center", md: "flex-end" }}
            paddingRight={{ base: "20px", md: "40px" }}
          >
            <Link href="/login">
              <Button bgColor="#4CAF4F" color="black" variant="solid">
                Login
              </Button>
            </Link>
          </Box>
        </Flex>
      </Box>
    </>
  );
};

export default NavBar;
