import React, { useEffect, useState } from "react";
import { Flex, Box, Spacer, Link, Img, Button } from "@chakra-ui/react";
import logo from "../assets/logo.png";
import "../styles/NavBar.css";

const NavBar = () => {
  const contact = "https://wa.me/6281215267243";

  return (
    <>
      <Box>
        <Flex
          top="0"
          left="0"
          right="0"
          zIndex="999"
          direction={{ base: "column", md: "row" }}
          justifyContent={{ base: "center", md: "space-between" }}
          alignItems={{ base: "center", md: "flex-start" }}
          className="myNavBar"
        >
          <Box
            paddingLeft={{ base: "0", md: "40px" }}
            color="white"
            display="flex"
            justifyContent={{ base: "center", md: "flex-start" }}
          >
            <Link href="/">
              <Img
                src={logo}
                alt="Solo Mina Corp"
                width={{ base: "150px", md: "200px" }}
                height={{ base: "150px", md: "200px" }}
                borderRadius="5px"
              ></Img>
            </Link>
          </Box>
          <Spacer />
          <Box
            display="flex"
            justifyContent="space-between"
            gap="20px"
            alignContent="flex-end"
            paddingRight={{ base: "20px", md: "40px" }}
            marginTop={{ base: "4", md: "0" }}
          >
            <Link color="black" href="/">
              Home
            </Link>
            <Link color="black" href="/aboutus">
              About
            </Link>
            <Link color="black" href="/faq">
              FAQ
            </Link>
            <Link color="black" href={contact} target="blank">
              Contact
            </Link>
          </Box>
          <Box
            display="flex"
            alignItems="center"
            gap="20px"
            justifyContent={{ base: "center", md: "flex-end" }}
            paddingRight={{ base: "20px", md: "40px" }}
          >
            <Link href="/login">
              <Button bgColor="#4CAF4F" color="black" variant="solid">
                Login
              </Button>
            </Link>
          </Box>
        </Flex>
      </Box>
    </>
  );
};

export default NavBar;
