import { Box, Flex, Heading, Text, Input, TableContainer, Table, Thead, Tr, Th, Tbody, Td, Button, useDisclosure, Modal, ModalOverlay, ModalContent, ModalHeader, ModalCloseButton, ModalBody, FormControl, FormLabel, ModalFooter, Textarea, InputGroup, InputLeftElement, useToast} from "@chakra-ui/react";
import SidebarSuperAdmin from "../../components/SidebarSuperAdmin";
import { useEffect, useState } from "react";
import api from "../../services/api";
import { SearchIcon } from "@chakra-ui/icons";

const CustomerManajemen = () => {
    
    const { isOpen, onOpen, onClose } = useDisclosure();

    // toast
    const toast = useToast();

    // Second Modal Delete Data
    const [secondModal, setSecondModal] = useState(false);

    const closeSecondModal = () => setSecondModal(false);
    const openSecondModal = () => setSecondModal(true);

    // Third Modal Add Data Customer
    const [thirdModal, setThirdModal] = useState(false);

    const closeThirdModal = () => setThirdModal(false);
    const openThirdModal = () => setThirdModal(true);

    // state to save response data all customer
    const [dataCust, setDataCust] = useState([]);

    // get all customer data 
    const getAllCust = async () => {
        try {
            const response = await api.get('/api/v4/cust');
            setDataCust(response.data.payload);
        } catch (error) {
            console.log(error);
        }
    }

    // post new data customers to db 
    const addDataCust = async (e) => {
        try {
            e.preventDefault();

            // get data from event 
            const newData = {
                    name: e.target.name.value,
                    contact: e.target.contact.value,
                    address: e.target.address.value,
            };

            console.log(newData)

            const response = await api.post('/api/v4/cust', newData);

            if (response.data.status_code === 400) {
                console.log("error data")
                return toast({
                    title: 'Data Cannot Create',
                    description: `${response.data.message}`,
                    status: 'error',
                    duration: 1500,
                    isClosable: true,
                    position: "top",
                });
            }

            if (response.data.status_code === 200) {
                toast({
                    title: 'Data created.',
                    description: "You Have Created New Customer",
                    status: 'success',
                    duration: 1500,
                    isClosable: true,
                    position: "top",
                })
                    
                getAllCust();

                closeThirdModal();
            }
        } catch (error) {
            console.log(error);
        }
    }

    // console.log(dataCust)

    useEffect(() => {
        getAllCust();
    }, []);

    // Paginated 
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage] = [5];

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentDataCust = dataCust.slice(indexOfFirstItem, indexOfLastItem);

    // function arrow paginated 
    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    }

    
    return(
        <>
            
            <Box bgColor={"#F5F7FA"} bgSize={"cover"} height={"100vh"} display={"flex"}>
                <SidebarSuperAdmin/>

                <Box margin={"50px"} width={"7xl"} display={"flex"} flexDirection={"column"} marginLeft={"280px"}>
                    
                    <Box marginLeft={"10px"} marginTop={"20px"} >
                        <Heading>Customer Manajemen</Heading>
                    </Box>

                    <Box padding={"30px"} display={"flex"} flexDirection={"column"} bgColor={"#FFFFFF"} borderRadius={"30px"} marginTop={"40px"} minHeight={"75vh"}>
                        <Flex flexDir={"row"} gap={860}>
                            <Box width={"190px"} alignSelf={"flex-start"}>
                                <Text marginBottom={"15px"}>Customer Name</Text>
                                <InputGroup>
                                    <InputLeftElement> 
                                        <SearchIcon/>
                                    </InputLeftElement>

                                    <Input placeholder="Name"></Input>
                                </InputGroup>
                            </Box>

                            <Box alignSelf={"flex-end"}>
                                <Button bgColor={"#4CAF4F"} borderRadius={"10px"} onClick={openThirdModal}>+ Add</Button>
                            </Box>
                        </Flex>

                        <Box bgColor={"whitesmoke"} boxSize={"6xl"} marginTop={"25px"} borderRadius={"20px"} marginLeft={"10px"}>
                            <TableContainer>
                                <Table variant={"striped"}>
                                    <Thead>
                                        <Tr>
                                            <Th>Id</Th>
                                            <Th>Name</Th>
                                            <Th>Contact</Th>
                                            <Th>Address</Th>
                                            <Th paddingLeft={"55px"}>Action</Th>
                                        </Tr>
                                    </Thead>
                                    <Tbody>
                                        {dataCust && dataCust.map((cust) => (
                                        <Tr key={cust.id}>
                                            <Td>{cust.id}</Td>
                                            <Td>{cust.name}</Td>
                                            <Td>{cust.contact}</Td>
                                            <Td>{cust.address}</Td>
                                            <Td>
                                                <Flex gap={"2"}>
                                                    <Button onClick={onOpen} size={"sm"} colorScheme="blue" borderRadius={"40px"}>Edit</Button>
                                                    <Button onClick={openSecondModal} size={"sm"} colorScheme="red" borderRadius={"40px"}>Delete</Button>
                                                </Flex>    
                                            </Td>                                
                                        </Tr>
                                        ))}
                                    </Tbody>
                                </Table>
                            </TableContainer>
                        </Box>    
                    </Box>
                </Box>
            </Box>   
            
            {/* Modal Add Data Customers */}
            <Modal isOpen={thirdModal} onClose={closeThirdModal} size={"2xl"}>
                <ModalOverlay backdropBlur={"2px"} backdropInvert={"80%"}/>

                <ModalContent>
                    <ModalHeader>Add Data Customers</ModalHeader>
                    <ModalCloseButton/>

                    <ModalBody >
                        <FormControl isRequired width={"100%"}>
                            <form onSubmit={(e) => {addDataCust(e)}}>
                                <Box width={"55%"}>
                                    <FormLabel>Name</FormLabel>
                                    <Input placeholder="Name" type="text" name="name" marginBottom={"20px"} ></Input>

                                    <FormLabel>Contact</FormLabel>
                                    <InputGroup>
                                        <InputLeftElement color={"GrayText"}>+62</InputLeftElement>
                                        <Input placeholder="81215267243" type="text" name="contact" marginBottom={"20px"}></Input>
                                    </InputGroup>

                                    <FormLabel>Address</FormLabel>
                                    <Textarea placeholder="Address" type="text" name="address" marginBottom={"20px"} resize={"horizontal"}></Textarea>
                                        
                                    <Button marginTop={"15px"} marginBottom={"15px"} type="submit">Add</Button>
                                </Box>        
                            </form>
                        </FormControl>
                    </ModalBody>
                </ModalContent>
            </Modal>

            {/* Modal Edit  */}

            <Modal isOpen={isOpen} onClose={onClose} size={"2xl"}>
                <ModalOverlay backdropBlur={"2px"} backdropInvert={"80%"}/>

                <ModalContent>
                    <ModalHeader>Edit Data Customer</ModalHeader>
                    <ModalCloseButton/>

                    <ModalBody >
                        <FormControl isRequired width={"100%"}>
                                <Box width={"55%"}>
                                    <FormLabel>Id</FormLabel>
                                    <Input placeholder="Id" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Name</FormLabel>
                                    <Input placeholder="Name" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Contact</FormLabel>
                                    <Input placeholder="Contact" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Address</FormLabel>
                                    <Textarea placeholder="Address" marginBottom={"20px"} resize={"horizontal"}></Textarea>
                                    

                                    <Button marginTop={"15px"} marginBottom={"15px"}>Edit</Button>
                                </Box>
                        </FormControl>
                    </ModalBody>
                </ModalContent>
            </Modal>

            {/* Second modal Delete */}
            <Modal isOpen={secondModal} onClose={closeSecondModal} size="sm">
                <ModalOverlay />
                <ModalContent>
                <ModalHeader>Are you sure ?</ModalHeader>
                <ModalCloseButton />
                <ModalBody>
                    Are you sure to delete this customer ?
                </ModalBody>
                <ModalFooter>
                    <Flex gap={"2"}>
                        <Button colorScheme="blue" onClick={closeSecondModal}>No</Button>
                        <Button colorScheme="green" onClick={closeSecondModal}>Yes</Button>
                    </Flex>
                </ModalFooter>
                </ModalContent>
            </Modal>

        </>
    )
};

export default CustomerManajemen;
