import { Routes, Route } from "react-router-dom";
import React from "react";
import { useEffect } from "react";

// routes
import Login from './auth/Login';
import LandingPage from './routes/LandingPage';
import ForgotPassword from "./auth/ForgotPassword";
import About from "./routes/About";

//admin routes
import Home from "./routes/admin/Home";
import SalesInfo from "./routes/admin/SalesInformation";

function App() {

  const routeConfig = [
    // {path: "/", component: LandingPage},
    {path: "/login", component: Login},
    {path: "/forgotPassword", component: ForgotPassword},
    {path: "/aboutus", component: About},

  ];

  const adminRoute = [
    {path: "/admin/dashboard", component: Home},
    {path: "/admin/salesInfo", component: SalesInfo},
  ]

  const publicRoutes = routeConfig.map((route, index) => (
    <Route key={index} path={route.path} element={<route.component/>}></Route>
  ));

  const protectRoutes = adminRoute.map((adminRoutes, index) => (
    <Route key={index} path={adminRoutes.path} element={<adminRoutes.component/>}></Route>
  ));

  useEffect(() => {
    return () => {
      window.location("/login");
    }
  }, []);


  return (
    <>

      <Routes>
        {publicRoutes}
        {protectRoutes}
      </Routes>

    </>
  );  
};

export default App;
