import { Box, Button, Heading, SimpleGrid, Image, Center, Flex } from "@chakra-ui/react";
import React from "react";
import Slider from "react-slick";
import { Fade } from 'react-slideshow-image';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import 'react-slideshow-image/dist/styles.css';
import NavBar from "../components/Navigasi";
import fish from "../assets/fish.png";
import arrow from "../assets/arrow.png";
import lotte from "../assets/lottemart.png";
import cianjur from "../assets/cianjur.png";
import pandowo from "../assets/pandowo.png";
import orient from "../assets/orient.png";
import mangEngking from "../assets/mangengking.png";

const LandingPage = () => {

    //Slider Image Logo
        const settings = {
          dots: true,
          infinite: true,
          speed: 500,
          slidesToShow: 5,
          slidesToScroll: 1,
        }
    //images 
    const fadeImages = [
        {
            url: lotte,
            alt: "lottemart",
        },
        {
            url: cianjur,
            alt: 'cianjur'
        },
        {
            url: pandowo,
            alt: "pandowo"
        },
        {
            url: orient,
            alt: "orient"
        },
        {
            url: mangEngking,
            alt: "mangEngking"
        },
    ];


    return(
        <>
        <NavBar/>
        <Box height="100vh" position="relative" bgSize="cover" bgImage="linear-gradient(to bottom, #FFFFFF , transparent)">
            <SimpleGrid columns={2} spacing={10}>
                <Box paddingLeft='60px' >
                    <Heading fontSize='8xl' paddingTop='52' as='h1' textColor="#4D4D4D"> Solo Mina Fish </Heading>
                    <Heading paddingTop='6' size='2xl' as='h3' textColor='#4CAF4F'> Since 20 Years</Heading>
                    <Heading paddingTop='8' as='h3'>Fish Consumption Supplier</Heading>
                    <Box paddingTop='10'>
                        <Button size='lg' bgColor='#4CAF4F' textColor='white'>Contact</Button>
                    </Box>
                </Box>
                <Box paddingTop='44' paddingLeft='32'>
                    <Image src={fish} alt="fish assets" width='450px' height='450px'></Image>
                </Box>
            </SimpleGrid>
            <Flex justifyContent='flex-end' marginTop='60px'>
                <Image src={arrow} boxSize="40px"></Image>
            </Flex>
        </Box>

        <Box height='100vh'position="relative" bgColor="#FFFFFF" bgSize="cover" marginTop='30px'>
            <Center textColor="#4D4D4D" fontWeight='semibold' fontSize='2xl'>
                Our Customers
            </Center>
            <Center textColor="#4D4D4D" fontWeight='normal' fontSize='xl'>
                We have been supplied with some 100+ customers
            </Center>

            <Box display="flex" justifyContent="space-between" marginTop="40px">
                    {fadeImages.map((image, index) => (
                        <Image key={index} src={image} alt={`customer-${index}`} width="220px" height="140px"></Image>
                    ))}
                </Box>
        </Box>
        </>
    )
};

export default LandingPage;
