import { Box, Flex, Image, Link, Text, useDisclosure, useToast } from "@chakra-ui/react";
import logo from "../assets/logo_2.png";
import { useLocation } from "react-router-dom";

// icon
import DashboardIcon from "../assets/dashboardIcon.svg";
import SalingInformation from "../assets/salingInformation.svg";
import React from "react";

const Sidebar = () =>{

    // Sidebar Menus

    const SideBarMenusAdmin = [
        {
            path: "/admin/dashboard",
            name: "Dashboard",
            icon: DashboardIcon,
        },
        {
            path: "/admin/salesInfo",
            name: "Saling Data",
            icon: SalingInformation,
        },
    ];

    const {pathname} = useLocation();

    const toast = useToast();

    // alert dialog 
    const {isOpen, onOpen, onClose} = useDisclosure()
    const cancelRef = React.useRef()

    // logout function remove token 
    const handleLogout = async () => {
        // remove token from localstorage
        try {
            await localStorage.removeItem('access_token');
    
            onClose();
    
            // alert 
            toast({
                title: "You're Loggin Out!",
                status: 'success',
                duration: 2000,
                isClosable: true,
                position: 'top',
            });
    
            setTimeout(() => {
                window.location.href = '/login'
            }, 2000);
            
        } catch (error) {
            console.log(error)
        }
    }

    return(
        <>
        <Box minHeight={"100vh"} bgColor={"black"} textColor={"white"} width={"220px"}>
           <Image src={logo} marginLeft={"10px"} marginTop={"65px"}></Image>

           <Flex direction={"column"} justifyContent={"space-between"}>
                <Box paddingTop={"80px"} paddingLeft={"30px"}>
                    {SideBarMenusAdmin.map((menu) => (
                        <Link key={menu.path} href={menu.path}>
                            <Flex alignItems={"center"} gap={"3"} marginBottom={"9"} cursor={"pointer"}>
                                <Image src={menu.icon} alt={menu.name}></Image>
                                <Text color={pathname.includes(menu.path) ? "#3686F7" : "white"} fontWeight={pathname.includes(menu.path) ? "700" : "500"} > 
                                    {menu.name}
                                </Text>
                            </Flex>
                        </Link>
                    ))}
                </Box>
           </Flex>
        </Box>
        </>
    )
};

export default Sidebar;
