import { Box, Button, FormControl, TableContainer, Heading, Thead, Tr, Th, Td, Table, Flex, Tbody, Modal, ModalOverlay, ModalContent, ModalHeader, ModalCloseButton, ModalBody, useDisclosure, FormLabel, Input, AlertDialog, AlertDialogOverlay, AlertDialogContent, AlertDialogHeader, AlertDialogBody, AlertDialogFooter, InputGroup, InputLeftElement, useToast, Text, Select } from "@chakra-ui/react";
import SidebarSuperAdmin from "../../components/SidebarSuperAdmin";
import api from "../../services/api";
import React, { useEffect, useState } from "react";
import { ArrowLeftIcon, ArrowRightIcon, SearchIcon } from "@chakra-ui/icons";

const PriceByCustomer = () => {

    // state for paginations 
    const [dataPrice, setDataPrice] = useState([]);
    const [page, setPage] = useState(1);

    // state for save data customers
    const [nameCustomers, setNameCustomers] = useState([]);
    // console.log(nameCustomers)

    // modal open and close 
    const [openAddModal, setOpenAddModal] = useState(false);

    // toast for alert 
    const toast = useToast();

    // state error status
    const [lastErrorStatus, setLastErrorStatus] = useState(null);

    // handle paginated
    const handleNextPage = () => {
        setPage(page + 1);
    };

    const handlePreviousPage = () => {
        if (page > 1) {
            setPage(page -1)
        }
    };

    // get all data price by customer 
    const getAllPriceData = async () => {
        try {
            const response = await api.get(`/api/v5/price/page?page=${page}`);
            setDataPrice(response.data.payload);
        } catch (error) {
            console.log("Error Fetching data", error);
            toast({
                title: 'Error.!',
                description: 'Internal Server Error',
                status: 'error',
                duration: 1500,
                isClosable: true,
                position: 'top',
            });
        }
    };

    // get all data customers by name 
    const getAllCustomersName = async () => {
        try {
            const response = await api.get(`/api/v4/cust/customer/name`);
            setNameCustomers(response.data.payload);
        } catch (error) {
            console.log("Error Fetching Data", error);
            toast({
                title: 'Error.!',
                description: 'Internal Server Error',
                status: 'error',
                duration: 1500,
                isClosable: true,
                position: 'top',
            });
        }
    };

    // get all fish name
    const getAllFishName = async() => {
        try {
            const response = await api.get(`/api/v3/fish/fishes/name`);
            console.log(response)
        } catch (error) {
            console.log("Error Fetching Data", error);
            toast({
                title: 'Error.!',
                description: 'Internal Server Error',
                status: 'error',
                duration: 1500,
                isClosable: true,
                position: 'top',
            });
        }
    }

    // get price cust by name 

    const getAllPriceByCustomerName = async (e) => {
        try {
            const name = e.target.value;

            // console.log(name)

            const response = await api.get(`/api/v5/price/name/page?name=${name}&page=${page}`)
            setDataPrice(response.data.payload)
            console.log(response.data.payload.data)
        } catch (error) {
            console.log("Error Fetching data", error);
            // if (error.response.data.status_code !== 200) {
            //     if (error.response.data.status_code !== lastErrorStatus) {
            //         setLastErrorStatus(error.response.data.status_code);
            //         toast({
            //             title: 'Cannot Show Data',
            //             description: `${error.response.data.message}`,
            //             status: 'error',
            //             duration: 1500,
            //             isClosable: true,
            //             position: "top",
            //         });
            //     }
            // }
        }
    };

    useEffect(() => {
        getAllPriceData();
    }, [page]);
    return(
        <>
        <Box bgColor={"#F5F7FA"} bgSize={"cover"} height={"100vh"} display={"flex"}>
                <SidebarSuperAdmin/>

                <Box margin={"50px"} width={"7xl"} display={"flex"} flexDirection={"column"} marginLeft={"280px"}>
                   
                   <Box marginLeft={"10px"} marginTop={"15px"} >
                        <Heading>Price By Customer</Heading>
                    </Box>

                    <Box padding={"30px"} display={"flex"} flexDirection={"column"} bgColor={"#FFFFFF"} borderRadius={"30px"} marginTop={"40px"} minHeight={"78vh"}>
                        <Flex flexDir={"row"} marginLeft={"13px"} gap={800}>
                            <Box alignSelf={"flex-start"} width={"190px"}>
                                <Text marginBottom={"10px"}>Customer Name</Text>
                                <InputGroup>
                                    <InputLeftElement>
                                        <SearchIcon/>
                                    </InputLeftElement>

                                    <Input placeholder="Customer Name" name="name" onChange={(e) => getAllPriceByCustomerName(e)}></Input>
                                </InputGroup>
                                </Box>

                            <Box alignSelf={"flex-end"}>
                                <Button bgColor={"#4CAF4F"} borderRadius={"10px"} onClick={() => {setOpenAddModal(true); getAllCustomersName(); getAllFishName()}}>+ Add</Button>
                            </Box>
                        </Flex>
                        
                        <Box bgColor={"whitesmoke"} boxSize={"6xl"} marginTop={"25px"} borderRadius={"20px"} marginLeft={"10px"} paddingBottom={"20px"} minHeight={"51.5vh"} maxHeight={"51.5vh"}>
                        <TableContainer>
                                <Table variant={"striped"} >
                                    <Thead>
                                        <Tr>
                                            <Th>Id</Th>
                                            <Th>Customer Name</Th>
                                            <Th>Fish Name</Th>
                                            <Th>Price</Th>
                                            <Th paddingLeft={"55px"}>Action</Th>
                                        </Tr>
                                    </Thead>
                                    <Tbody>
                                        {dataPrice.data && dataPrice.data.map((prices) => (
                                        <Tr key={prices.id}>
                                            <Td>{prices.id}</Td>
                                            <Td>{prices.customer.name}</Td>
                                            <Td>{prices.fish.name}</Td>
                                            <Td>{`Rp. ${prices.price.toLocaleString('id-ID')}`}</Td>
                                            <Td>
                                                <Flex gap={"2"}>
                                                    <Button size={"sm"} colorScheme="blue" borderRadius={"40px"} >Edit</Button>
                                                    <Button size={"sm"} colorScheme="red" borderRadius={"40px"} >Delete</Button>
                                                </Flex>    
                                            </Td>                                
                                        </Tr>
                                        ))}
                                    </Tbody>
                                </Table>
                            </TableContainer>
                        </Box>

                        <Flex marginTop={"20px"} gap={"10px"} marginLeft={"15px"}>
                            <Box as="div" marginTop={"7px"} justifyContent={"flex-start"}>
                                <Text fontSize={"sm"}>Current Pages : {dataPrice.currentPages}</Text>
                            </Box>

                            <Box as="div" marginTop={"7px"}>
                                <Text fontSize={"sm"}>Total Pages : {dataPrice.totalPages}</Text>
                            </Box>

                            <Button marginLeft={"240px"} bgColor={"#b6e1e0"} alignItems={"center"} display={"flex"} marginBottom={"5px"} onClick={handlePreviousPage} justifyContent={"center"} size={"sm"}>
                                <ArrowLeftIcon/> 
                                <Text marginLeft={"5px"} marginBottom={"3px"}>
                                    Prev
                                </Text>
                            </Button>
                        

                            {/* <Flex gap={"5px"}>
                                {Array.from({length: Math.ceil(dataFish.length / itemsPerPage)}).map((_, index) => (
                                    <Button size={"sm"} key={index} onClick={() => paginate(index + 1)} colorScheme={currentPage === index + 1 ? "teal" : "gray"}>
                                        {index + 1}
                                    </Button>
                                ))}
                            </Flex> */}

                            <Button onClick={handleNextPage} marginBottom={"5px"} bgColor={"#b6e1e0"} alignItems={"center"} display={"flex"} justifyContent={"center"} size={"sm"}>
                                <ArrowRightIcon/>
                                <Text marginLeft={"5px"} marginBottom={"3px"}>
                                    Next
                                </Text>
                            </Button>
                        </Flex>
                    </Box>
                </Box>
            </Box>

            {/* modal to add data price by customers  */}
            <Modal isOpen={openAddModal} onClose={() => setOpenAddModal(false)} size={"xl"}>
                <ModalOverlay backdropBlur={"2px"} backdropInvert={"80%"}>
                    <ModalContent>
                        <ModalHeader>Add Data Price By Customers</ModalHeader>
                        <ModalCloseButton/>
                        
                        <ModalBody>
                            <FormControl isRequired width={"100%"}>
                                <Box width={"70%"}>
                                    <Flex flexDir={"column"} gap={5}>
                                        <Box>
                                            <FormLabel>Customer Name</FormLabel>
                                            <Select placeholder="Select Customer Name">
                                                {nameCustomers && nameCustomers.map((customer, index) => (
                                                    <option key={index} value={customer.id}>{customer.name}</option>
                                                ))}
                                            </Select>
                                        </Box>

                                        <Box>
                                            <FormLabel>Fish Name</FormLabel>
                                            <Input></Input>
                                        </Box>

                                        <Box>
                                            <FormLabel>Price</FormLabel>
                                            <Input></Input>
                                        </Box>

                                        <Box>
                                            <Button>Add</Button>
                                        </Box>
                                    </Flex>
                                </Box>
                            </FormControl>
                        </ModalBody>
                    </ModalContent>
                </ModalOverlay>
            </Modal>
        </>
    )
}

export default PriceByCustomer;
