import { Box, Flex, Heading, Select, Text, Input, TableContainer, Table, Thead, Tr, Th, Tbody, Td, Button, useDisclosure, Modal, ModalOverlay, ModalContent, ModalHeader, ModalCloseButton, ModalBody, FormControl, FormLabel, ModalFooter, Avatar} from "@chakra-ui/react";
import SidebarSuperAdmin from "../../components/SidebarSuperAdmin";
import { useEffect, useState } from "react";
import api from "../../services/api"

const AdminManajemen = () => {
    
    const { isOpen, onOpen, onClose } = useDisclosure();

    // Second Modal Delete Data
    const [secondModal, setSecondModal] = useState(false);

    const closeSecondModal = () => setSecondModal(false);
    const openSecondModal = () => setSecondModal(true);

    // Third Modal Add Data Admin
    const [thirdModal, setThirdModal] = useState(false);

    const closeThirdModal = () => setThirdModal(false);
    const openThirdModal = () => setThirdModal(true);

    // state management untuk menyimpan selected name 
    const [selectedUserId, setSelectedUserId] = useState(0);
    // console.log(selectedUserId)

    // function handle select change by name
    const handleSelectChange = (event) => {
        setSelectedUserId(event.target.value);

        if (selectedUserId) {
            getUserId()
        };
    };

    // state to all users 
    const [allUsers, setAllUsers] = useState([]);

    // state data to save data users
    const [dataUsers, setDataUsers] = useState([]);

    // get all users data from axios 

    const getUser = async () => {
        try {
            const response = await api.get(`/api/v1/users`);
            setDataUsers(response.data.payload);    
            setAllUsers(response.data.payload);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };    

    // get data users by id 

    const getUserId = async () => {
        try {
            if (selectedUserId !== undefined) {
                const response = await api.get(`/api/v1/users/${selectedUserId}`);
                setDataUsers(response.data.payload);
            }else{
                console.error("selectedUserId is undefined");
            }
        } catch (error) {
            console.error("Error fetching data:", error )
        }
    };

    // console.log(dataUsers)

    // Component did mount start method
    useEffect(() => {
        getUser();
    }, []);
    
    return(
        <>
            
            <Box bgColor={"#F5F7FA"} bgSize={"cover"} height={"100vh"} display={"flex"}>
                <SidebarSuperAdmin/>

                <Box margin={"50px"} width={"7xl"} display={"flex"} flexDirection={"column"} marginLeft={"280px"}>
                    
                    <Box marginLeft={"10px"} marginTop={"20px"} >
                        <Heading>Users Manajemen</Heading>
                    </Box>

                    <Box padding={"30px"} display={"flex"} flexDirection={"column"} bgColor={"#FFFFFF"} borderRadius={"30px"} marginTop={"40px"} minHeight={"75vh"}>
                        <Flex flexDir={"row"} gap={860}>
                            <Box width={"190px"} alignSelf={"flex-start"}>
                                <Text marginBottom={"15px"}>Admin</Text>
                                <Select variant={"outline"} placeholder="Name" value={selectedUserId} onChange={handleSelectChange}>
                                    {allUsers && allUsers.map((user) => (
                                        <option key={user.id} value={user.id}> 
                                            {user.name}
                                        </option>
                                    ))}
                                </Select>
                            </Box>

                            <Box alignSelf={"flex-end"}>
                                <Button bgColor={"#4CAF4F"} borderRadius={"10px"} onClick={openThirdModal}>+ Add</Button>
                            </Box>
                        </Flex>

                        <Box bgColor={"whitesmoke"} boxSize={"6xl"} marginTop={"25px"} borderRadius={"20px"} marginLeft={"10px"}>
                            <TableContainer>
                                <Table variant={"striped"}>
                                    <Thead>
                                        <Tr>
                                            <Th>Id</Th>
                                            <Th>Profile</Th>
                                            <Th>Name</Th>
                                            <Th>Username</Th>
                                            <Th>Password</Th>
                                            <Th>Role</Th>     
                                            <Th paddingLeft={"55px"}>Action</Th>
                                        </Tr>
                                    </Thead>
                                    <Tbody>
                                        {dataUsers && dataUsers.map(user => (
                                            <Tr key={user.id}>
                                                <Td>{user.id}</Td>
                                                <Td>
                                                    {user.profile ? (
                                                        <Avatar size={"md"} name={user.name} src={user.profile}></Avatar>
                                                    ): (
                                                        <Avatar size={"md"} name={user.name} bgColor={"GrayText"}></Avatar>
                                                    )} 
                                                </Td>
                                                <Td>{user.name}</Td>
                                                <Td>{user.username}</Td>
                                                <Td>{user.password}</Td>
                                                <Td>{user.role}</Td>
                                                <Td>
                                                    <Flex gap={"2"}>
                                                        <Button onClick={onOpen} size={"sm"} colorScheme="blue" borderRadius={"40px"}>Edit</Button>
                                                        <Button onClick={openSecondModal} size={"sm"} colorScheme="red" borderRadius={"40px"}>Delete</Button>
                                                    </Flex>    
                                                </Td>                                
                                            </Tr>
                                        ))}
                                    </Tbody>
                                </Table>
                            </TableContainer>
                        </Box>    
                    </Box>
                </Box>
            </Box>   
            

            {/* Modal Edit  */}

            <Modal isOpen={isOpen} onClose={onClose} size={"5xl"}>
                <ModalOverlay backdropBlur={"2px"} backdropInvert={"80%"}/>

                <ModalContent>
                    <ModalHeader>Edit Data Admin</ModalHeader>
                    <ModalCloseButton/>

                    <ModalBody >
                        <FormControl isRequired width={"100%"}>
                            <Flex flexDir={"row"} gap={10}>
                                <Box>
                                    <Avatar size={"xl"} name='Dan Abrahmov' src='https://bit.ly/dan-abramov'></Avatar>
                                </Box>

                                <Box width={"40%"}>
                                    <FormLabel>Id</FormLabel>
                                    <Input placeholder="Id" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Username</FormLabel>
                                    <Input placeholder="Name" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Password</FormLabel>
                                    <Input placeholder="Password" type="text" marginBottom={"20px"}></Input>

                                    <Button marginTop={"15px"} marginBottom={"15px"}>Edit</Button>
                                </Box>

                                <Box width={"40%"}>
                                    <FormLabel>Role</FormLabel>
                                    <Input placeholder="Role" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Gender</FormLabel>
                                    <Select placeholder="Select Gender" marginBottom={"20px"}>
                                        <option>Male</option>
                                        <option>Female</option>
                                    </Select>
                                </Box>
                            </Flex>               
                        </FormControl>
                    </ModalBody>
                </ModalContent>
            </Modal>

            {/* Second modal Delete */}
            <Modal isOpen={secondModal} onClose={closeSecondModal} size="sm">
                <ModalOverlay />
                <ModalContent>
                <ModalHeader>Are you sure ?</ModalHeader>
                <ModalCloseButton />
                <ModalBody>
                    Are you sure to delete this admin ?
                </ModalBody>
                <ModalFooter>
                    <Flex gap={"2"}>
                        <Button colorScheme="blue" onClick={closeSecondModal}>No</Button>
                        <Button colorScheme="green" onClick={closeSecondModal}>Yes</Button>
                    </Flex>
                </ModalFooter>
                </ModalContent>
            </Modal>

            {/* Modal Add Data Admin */}
            <Modal isOpen={thirdModal} onClose={closeThirdModal} size={"5xl"}>
                <ModalOverlay backdropBlur={"2px"} backdropInvert={"80%"}/>

                <ModalContent>
                    <ModalHeader>Add Data Admin</ModalHeader>
                    <ModalCloseButton/>

                    <ModalBody >
                        <FormControl isRequired width={"100%"}>
                            <Flex flexDir={"row"} gap={10}>

                                <Box width={"40%"}>
                                    <FormLabel>Name</FormLabel>
                                    <Input placeholder="Name" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Username</FormLabel>
                                    <Input placeholder="Username" type="text" marginBottom={"20px"}></Input>

                                    <FormLabel>Password</FormLabel>
                                    <Input placeholder="Password" type="text" marginBottom={"20px"}></Input>

                                    <Button marginTop={"15px"} marginBottom={"15px"}>Add</Button>
                                </Box>

                                <Box width={"40%"}>
                                    <FormLabel>Role</FormLabel>
                                    <Select placeholder="Pick Role" marginBottom={"20px"}>
                                        <option>Admin</option>
                                        <option>Super Admin</option>
                                    </Select>

                                    <FormLabel>Gender</FormLabel>
                                    <Select placeholder="Select Gender" marginBottom={"20px"}>
                                        <option>Male</option>
                                        <option>Female</option>
                                    </Select>

                                    <FormLabel>Profile Picture</FormLabel>
                                    <Input type="file" placeholder="Profile Picture" paddingTop={"5px"}></Input>
                                </Box>
                            </Flex>               
                        </FormControl>
                    </ModalBody>
                </ModalContent>
            </Modal>
        </>
    )
};

export default AdminManajemen;
