import { Box, Button, FormControl, TableContainer, Heading, Thead, Tr, Th, Td, Table, Flex, Tbody, Modal, ModalOverlay, ModalContent, ModalHeader, ModalCloseButton, ModalBody, useDisclosure, FormLabel, Input, AlertDialog, AlertDialogOverlay, AlertDialogContent, AlertDialogHeader, AlertDialogBody, AlertDialogFooter, InputGroup, InputLeftElement } from "@chakra-ui/react";
import SidebarSuperAdmin from "../../components/SidebarSuperAdmin";
import api from "../../services/api";
import React, { useEffect, useState } from "react";
import { ArrowLeftIcon, ArrowRightIcon, PhoneIcon } from "@chakra-ui/icons";

const FishData = () => {

    // modal open close 
    const { isOpen, onClose, onOpen } = useDisclosure();
    const cancelRef = React.useRef();

    // confirmation modal open 
    const [confirmationOpen, setConfirmationOpen] = useState(false);
    
    // edit
    const [openModalEdit, setOpenModalEdit] = useState(false);

    // confirmation alert open 
    // delete
    const [openAlertDelete, setOpenAlertDelete] = useState(false);

    // state data to save response data fish
    const [dataFish, setDataFish] = useState([]);

    // fetching data from axios to get all fish data
    const getFish = async () => {
        try {
            const response = await api.get(`/api/v3/fish`);
            setDataFish(response.data.payload);
        } catch (error) {
            console.log(error);
        }
    }

    console.log(dataFish);

    useEffect(() => {
        getFish();
    }, []);

    // paginated 
    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage] = [5];

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentDataFish = dataFish.slice(indexOfFirstItem, indexOfLastItem);

    // function arrow paginated 
    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const nextPage = () => {
        if (currentPage < Math.ceil(dataFish.length / itemsPerPage)) {
            setCurrentPage(currentPage + 1);
        }
    };  

    const paginate = (pageNumber) => setCurrentPage(pageNumber);
    return(
        <>
            <Box bgColor={"#F5F7FA"} bgSize={"cover"} height={"100vh"} display={"flex"}>
                <SidebarSuperAdmin/>

                <Box margin={"50px"} width={"7xl"} display={"flex"} flexDirection={"column"} marginLeft={"280px"}>
                   
                   <Box marginLeft={"10px"} marginTop={"20px"} >
                        <Heading>Fish Data</Heading>
                    </Box>

                    <Box padding={"30px"} display={"flex"} flexDirection={"column"} bgColor={"#FFFFFF"} borderRadius={"30px"} marginTop={"40px"} minHeight={"75vh"}>
                        <Box>
                            <Button marginLeft={"90%"} bgColor={"#4CAF4F"} borderRadius={"10px"} onClick={() => setConfirmationOpen(true)}>+ Add</Button>
                        </Box>
                        
                        <Box bgColor={"whitesmoke"} boxSize={"6xl"} marginTop={"25px"} borderRadius={"20px"} marginLeft={"10px"} paddingBottom={"20px"}>
                        <TableContainer>
                                <Table variant={"striped"} >
                                    <Thead>
                                        <Tr>
                                            <Th>Id</Th>
                                            <Th>Name</Th>
                                            <Th>Minimal Price</Th>
                                            <Th>Maximal Price</Th>
                                            <Th paddingLeft={"55px"}>Action</Th>
                                        </Tr>
                                    </Thead>
                                    <Tbody>
                                        {currentDataFish && currentDataFish.map((fish) => (
                                        <Tr key={fish.id}>
                                            <Td>{fish.id}</Td>
                                            <Td>{fish.name}</Td>
                                            <Td>{`Rp. ${fish.min_price.toLocaleString('id-ID')}`}</Td>
                                            <Td>{`Rp. ${fish.max_price.toLocaleString('id-ID')}`}</Td>
                                            <Td>
                                                <Flex gap={"2"}>
                                                    <Button size={"sm"} colorScheme="blue" borderRadius={"40px"} onClick={() => setOpenModalEdit(true)}>Edit</Button>
                                                    <Button size={"sm"} colorScheme="red" borderRadius={"40px"} onClick={() => setOpenAlertDelete(true)}>Delete</Button>
                                                </Flex>    
                                            </Td>                                
                                        </Tr>
                                        ))}
                                    </Tbody>
                                </Table>
                            </TableContainer>
                        </Box>

                        <Flex justify={"center"} marginTop={"20px"} gap={"10px"}>
                            <Box as="button" onClick={prevPage} marginBottom={"5px"}>
                                <ArrowLeftIcon/>
                            </Box>

                            <Flex gap={"5px"}>
                                {Array.from({length: Math.ceil(dataFish.length / itemsPerPage)}).map((_, index) => (
                                    <Button size={"sm"} key={index} onClick={() => paginate(index + 1)} colorScheme={currentPage === index + 1 ? "teal" : "gray"}>
                                        {index + 1}
                                    </Button>
                                ))}
                            </Flex>

                            <Box as="button" onClick={nextPage} marginBottom={"5px"}>
                                <ArrowRightIcon/>
                            </Box>
                        </Flex>
                    </Box>
                </Box>
            </Box>

            {/* Modal Add Data Fish */}

            <Modal isOpen={confirmationOpen} onClose={() => setConfirmationOpen(false)}>
                <ModalOverlay backdropBlur={"2px"} backdropInvert={"80%"}>
                    <ModalContent>
                        <ModalHeader>Add Data Fish</ModalHeader>
                        <ModalCloseButton/>

                        <ModalBody>
                            <FormControl isRequired width={"100%"}>
                                <Box width={"50%"}>
                                    <Flex flexDir={"column"} gap={5}>
                                        <Box>
                                            <FormLabel>Fish Name</FormLabel>
                                            <Input></Input>
                                        </Box>

                                        <Box>
                                            <FormLabel>Minimal Price</FormLabel>
                                            <InputGroup>
                                                <InputLeftElement pointerEvents="none">
                                                    Rp.
                                                </InputLeftElement>
                                            <Input></Input>
                                            </InputGroup>
                                        </Box>

                                        <Box>
                                            <FormLabel>Minimal Price</FormLabel>
                                            <Input></Input>
                                        </Box>

                                        <Box marginBottom={"15px"}>
                                            <Button onClick={() => setConfirmationOpen(false)}>Add</Button>
                                        </Box>
                                    </Flex>
                                </Box>
                            </FormControl>
                        </ModalBody>
                    </ModalContent>
                </ModalOverlay>
            </Modal>

            {/* Modal for edit data fish */}

            <Modal isOpen={openModalEdit} onClose={() => setOpenModalEdit(false)}>
                <ModalOverlay backdropBlur="xl" backdropInvert="80%" backgroundBlendMode="darken"> 
                    <ModalContent>
                        <ModalHeader>Edit Data Fish</ModalHeader>
                        <ModalCloseButton/>

                        <ModalBody>
                            <form>
                                <FormControl>
                                    <Box width={"50%"}>
                                        <Flex flexDir={"column"} gap={5}>
                                            <Box>
                                                <FormLabel>Id</FormLabel>
                                                <Input></Input>
                                            </Box>

                                            <Box>
                                                <FormLabel>Name</FormLabel>
                                                <Input></Input>
                                            </Box>

                                            <Box>
                                                <FormLabel>Minimal Price</FormLabel>
                                                <Input></Input>
                                            </Box>

                                            <Box>
                                                <FormLabel>Maximal Price</FormLabel>
                                                <Input></Input>
                                            </Box>

                                            <Box marginBottom={"15px"}>
                                                <Button>Edit</Button>
                                            </Box>
                                        </Flex>                                 
                                    </Box>
                                </FormControl>
                            </form>
                        </ModalBody>
                    </ModalContent>
                </ModalOverlay>
            </Modal>

            {/* Alert dialog for delete data */}
            <AlertDialog isOpen={openAlertDelete} leastDestructiveRef={cancelRef} onClose={() => setOpenAlertDelete(false)}> 
                <AlertDialogOverlay>
                    <AlertDialogContent>
                        <AlertDialogHeader fontSize={"lg"} fontWeight={"bold"}>
                            Delete data
                        </AlertDialogHeader>

                        <AlertDialogBody>
                            Are you sure to Delete data fish ?
                        </AlertDialogBody>

                        <AlertDialogFooter>
                            <Button ref={cancelRef} onClick={() => setOpenAlertDelete(false)} marginRight={"10px"}>
                                Cancel
                            </Button>

                            <Button colorScheme="red" onClick={() => setOpenAlertDelete(false)}>
                                Delete
                            </Button>
                        </AlertDialogFooter>
                    </AlertDialogContent>
                </AlertDialogOverlay>
            </AlertDialog>
        </>
    )
}

export default FishData;
